<?php

namespace apps\common\entity;

/**
 * @Entity
 * @Table(name="bg_Plan_Type")
 */
class PlanType extends EntityBase {

    /**
     * @Id 
     * @Column(type="integer",length=11,name="Id")
     * @GeneratedValue
     */
    public $id;

    /** @Column(type="string",length=255, name="Name") */
    public $name;

    /** @Column(type="string",length=100, name="Code") */
    public $code;

    /** @Column(type="boolean",length=1, name="Is_Normal") */
    public $isNormal;

    /** @Column(type="string",length=100, name="Creator") */
    public $creator;

    /** @Column(type="string",length=100, name="Updater") */
    public $updater;

    function getId() {
        return $this->id;
    }

    function getName() {
        return $this->name;
    }

    function getCode() {
        return $this->code;
    }

    function getIsNormal() {
        return $this->isNormal;
    }

    function getCreator() {
        return $this->creator;
    }

    function getUpdater() {
        return $this->updater;
    }

    function setId($id) {
        $this->id = $id;
    }

    function setName($name) {
        $this->name = $name;
    }

    function setCode($code) {
        $this->code = $code;
    }

    function setIsNormal($isNormal) {
        $this->isNormal = $isNormal;
    }

    function setCreator($creator) {
        $this->creator = $creator;
    }

    function setUpdater($updater) {
        $this->updater = $updater;
    }

}
